                        //only rewrite colorPalette property when it is an orginal property
                        propMgr.origin.add('plotArea.secondaryValuesColorPalette', {
                            defaultValue: DUAL_COLOR_PALETTE[1],
                            serializable: false,
                            get: function() {
                                return colorPalette;
                            }
                        });
