define('sap/viz/framework/binding/flatTableChains/ComboExChains',[
    "sap/viz/framework/binding/flatTableChains/BaseChains"
], function(
    BaseChains
) {
    var ComboExChains = BaseChains.extend("combex", [
        "GetDimensionsFromFlattable",
        "GetMeasuresFromFlattable",
        "GetBindingSeries",
        "GetContext",
        "OnBindingFinished",
        "OnBindingStart"
    ]);

    function getSeries(rawData, dimensions, measures, bindingResults, bindings, bindingDefinition, options) {
        BaseChains.GetBindingSeries.apply(this, arguments); // will not work for non-bar/line/area charts

        bindingResults.series.forEach(function(sr) {
            var mndKey, mndIdx, i;

            for (i = 0; i < measures.length; i++) {
                if (sr.measureMeta.indexOf(measures[i]) > -1) {
                    mndKey = measures[i];
                    break;
                }
            }

            for (i = 0; i < dimensions.length; i++) {
                if (sr.seriesMeta[dimensions[i]] && sr.seriesMeta[dimensions[i]].hasOwnProperty("mndIndex")) {
                    mndIdx = sr.seriesMeta[dimensions[i]].mndIndex;
                    break;
                }
            }

            // series should conform its array interface as much as possible, so spec shall be non-enumerable
            Object.defineProperty(sr, "spec", {
                enumerable: false,
                value: {
                    mndKey: mndKey,
                    mndIdx: mndIdx
                }
            });
        });
    }

    ComboExChains.combexGetBindingSeries = getSeries;

    return ComboExChains;
});

