        log.apply(this, ["Creating context: " + bundleId]);

        var deferred = Q.defer();

        var fullBaseUrl = getFullUrl(bundleBaseUrl, convertBundleIdToPath(bundleId), bundleId);
        this._bundlePaths[fullBaseUrl] = bundleId;

